//     phone: "+918 372 574",
//     email: "gmail@chucknorris.com"
//   }
// ];



function App(props) {
  return (
    <div>
      <div className="card">
        <div className="top">
          <h2 className="name">{props.name}</h2>
          <img className="circle-img"
            src={props.img}
            alt="avatar_img"
          />
        </div>
        <div className="bottom">
          <p className="info">{props.ph}</p>
          <p className="info">{props.mail}</p>
        </div>
      </div>
    </div>
  );
}
function App2(){
  return (<div>
  <h1 className="heading">My Contacts</h1>
<App name = {contacts[0].name}
       img = {contacts[0].imgURL}
       ph = {contacts[0].phone}
       mail = {contacts[0].email}
  />,
  <App name = {contacts[1].name}
  img = {contacts[1].imgURL}
  ph = {contacts[1].phone}
  mail = {contacts[1].email}
/>,
<App name = {contacts[2].name}
  img = {contacts[2].imgURL}
  ph = {contacts[2].phone}
  mail = {contacts[2].email}
/>
  </div>
  )

}

export default App2;
